module.exports = function (pageMeta) {
  const experimentationRecipes = pageMeta.context ? pageMeta.context.customerExperiment : '';
  const fourColumnGridExpEnabled = pageMeta.properties ? pageMeta.properties.fourColumnGridExpEnabled : '';
  const mltSimilarProductsPage = pageMeta.pageType ? pageMeta.pageType.similarProducts : '';
  if (
    (fourColumnGridExpEnabled &&
    experimentationRecipes &&
    (experimentationRecipes.indexOf('194-22') !== -1 || experimentationRecipes.indexOf('194-23') !== -1)) ||

    mltSimilarProductsPage
  ) {
    return true;
  }
  return false;
};
