function IABOPSTabLayoutEnabled(PageApp) {
  return !!PageApp.getMeta('properties.itemAvailabilityBOPSTabLayoutEnabled');
}

function IAOptimizedBOPSExpEnabled(PageApp) {
  const IAOptimizedBOPSEnabled = IABOPSTabLayoutEnabled(PageApp) && PageApp.getMeta('properties.itemAvailabilityOptimizedBOPSExperienceEnabled');
  const expRecipes = PageApp.getMeta('context.customerExperiment');
  if (IAOptimizedBOPSEnabled && expRecipes) {
    return PageApp.brand === 'mcom' ? true : expRecipes.indexOf('59-22') !== -1;
  }
  return false;
}

function itemAvailabilityBOPSTabLayoutOnly(PageApp) {
  return IABOPSTabLayoutEnabled(PageApp) && !IAOptimizedBOPSExpEnabled(PageApp);
}

export default {
  itemAvailabilityBOPSTabLayoutEnabled: IABOPSTabLayoutEnabled,
  itemAvailabilityOptimizedBOPSExperienceEnabled: IAOptimizedBOPSExpEnabled,
  itemAvailabilityBOPSTabLayoutOnly,
};
