function ocfPickupBopsBossBadgingEnabled(PageApp) {
  const brand = PageApp.brand;
  const expRecipes = PageApp.getMeta('context.customerExperiment');
  const ocfPickupBopsBossBadgingKS = PageApp.getMeta('properties.ocfPickupBossBadgingEnabled') || PageApp.getMeta('properties.ocfPickupBopsBadgingEnabled');
  if (brand === 'mcom' && ocfPickupBopsBossBadgingKS && expRecipes) {
    return expRecipes.indexOf('212-21') !== -1;
  }
  return false;
}

function ocfPickupBopsFilterEnabled(PageApp) {
  const brand = PageApp.brand;
  const expRecipes = PageApp.getMeta('context.customerExperiment');
  const ocfPickupBopsFilterKS = PageApp.getMeta('properties.ocfPickupBopsFilterEnabled');
  if (brand === 'mcom' && ocfPickupBopsFilterKS && expRecipes) {
    return expRecipes.indexOf('208-22') !== -1;
  }
  return false;
}

function ocfPickupBOPSOnlyAllProductViewsEnabled(PageApp) {
  const brand = PageApp.brand;
  const expRecipes = PageApp.getMeta('context.customerExperiment');
  const ocfBopsOnlyAllProductViewsKS = PageApp.getMeta('properties.ocfPickupBOPSOnlyAllProductViewsEnabled');
  if (expRecipes && ocfBopsOnlyAllProductViewsKS) {
    return brand === 'mcom' ? expRecipes.indexOf('206-21') !== -1 : expRecipes.indexOf('222-21') !== -1;
  }
  return false;
}

export default {
  ocfPickupBopsBossBadgingEnabled,
  ocfPickupBOPSOnlyAllProductViewsEnabled,
  ocfPickupBopsFilterEnabled,
};
