module.exports = function (model) {
  const tagManager = require('../TagManagerUtil');
  if (model) {
    const product = model.product || {};
    const utagData = {
      event_name: 'view full product',
      product_id: [product.id ? product.id.toString() : ''],
    };

    tagManager.fireTag('link', utagData);
  }
};
