module.exports = function (response) {
  const tagManager = require('../TagManagerUtil');
  const _ = require('underscore');
  if (response) {
    const utagData = {
      page_type: 'wishlist',
      product_id: [],
      product_name: [],
      product_price: [],
      product_quantity: [],
      product_upc: [],
    };

    _.each(response, (val) => {
      utagData.product_id.push(val.productId ? val.productId.toString() : '');
      utagData.product_name.push(val.productName);
      utagData.product_price.push(val.unitPrice ? val.unitPrice.toString() : '');
      utagData.product_quantity.push(val.qtyRequested ? val.qtyRequested.toString() : '');
      utagData.product_upc.push(val.upcNumber);
    });

    tagManager.fireTag('view', utagData);
  }
};
