import HorizontalCarousel from './views/HorizontalCarousel';
import VerticalCarousel from './views/VerticalCarousel';
import { HORIZONTAL, VERTICAL } from './views/Carousel';

export {
  default as Carousel,
  ArrowIconSize,
  ScrollType,
  NEXT_EVENT,
  PREV_EVENT,
  READY_EVENT,
  LAST_PAGE_EVENT,
  PAGE_SHOWN_EVENT,
  SWIPE_EVENT,
  HORIZONTAL,
  VERTICAL,
} from './views/Carousel';

/**
 * Factory function for carousel component.
 *
 * @param carouselType
 * @param options
 * @return {*}
 */
export function createCarouselView(carouselType, options) {
  let view = null;
  if (carouselType === HORIZONTAL) {
    view = new HorizontalCarousel(options);
  } else if (carouselType === VERTICAL) {
    view = new VerticalCarousel(options);
  }
  return view;
}

export { VerticalCarousel, HorizontalCarousel };
