function showLabel(label, options) {
  if (typeof label === 'string') {
    const lbl = label.toLowerCase();
    /* Removing Reg/Orig price labels */
    if (lbl.indexOf('reg') !== -1 || lbl.indexOf('orig') !== -1) {
      return options.inverse(this);
    }
    return options.fn(this);
  }
  /* Default is to show label */
  return options.fn(this);
}
module.exports = showLabel;
