require(`./scss/${process.env.ENTRY_POINT}/product-thumbnail.scss`);

function Component() {
  return {
    ProductThumbnail(model, element) {
      return import(/* webpackChunkName: "productThumbnailView" */ `./views/${process.env.ENTRY_POINT}/ProductThumbnail`).then((ProductThumbnailView) => {
        // eslint-disable-next-line new-cap
        const view = new ProductThumbnailView.default({ model });
        if (element) {
          view.setElement(element);
        }
        return view;
      });
    },
    ProductThumbnailView() {
      return import(/* webpackChunkName: "productThumbnailView" */ `./views/${process.env.ENTRY_POINT}/ProductThumbnail`).then(view => view.default);
    },
  };
}

export default Component;
